import React, { createContext, useState } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const loginUser = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });

            if (response.data) {
                localStorage.setItem('token', response.data.token);
                setUser(response.data.user); // Set the user data from the response
                setIsAuthenticated(true); // Mark as authenticated
                console.log('Login successful:', response.data); // Optional logging
            } else {
                console.error('Login failed: No response data');
            }
        } catch (error) {
            console.error('Error logging in:', error.response?.data || error.message);
            setIsAuthenticated(false);
        }
    };

    const logout = () => {
        setUser(null);
        setIsAuthenticated(false);
    };


    const registerUser = async (name, email, password) => {
        try {
            const data = await register(name, email, password);
            setUser(data); // რეგისტრაციის შემდეგ შეიძლება, დავაბრუნოთ JWT
            localStorage.setItem('user', JSON.stringify(data));
        } catch (error) {
            console.error('Registration failed:', error);
        }
    };

    return (
        <AuthContext.Provider value={{ user, loginUser, isAuthenticated, logout }}>
            {children}
        </AuthContext.Provider>
    );


};
